const defaultState = {
  list: ['hello', 'world', 'React'],
  inputValue: '2222'
}

export default (state = defaultState, action) => {
  console.log('state:', state, 'action', action)
  switch (action.type) {
    case 'handle_input_change':
      let newstate = JSON.parse(JSON.stringify(state))
      newstate.inputValue = action.value
      return state

  }
  return state
}
