const defaultState = {
  list: ['hello', 'world', 'React'],
  inputValue: '2222'
}

export default (state = defaultState, action) => {
  switch (action.type) {
    case 'handle_input_change':
      let state = JSON.parse(JSON.stringify(state))
      state.inputValue = action.value
      return state
    default:
      return state
  }
}
