import React from 'react'
import store from './store'

let data = ''
class B extends React.Component {
  constructor(props) {
    super(props);
    this.getStateData = this.getStateData.bind(this);
    store.subscribe(this.getStateData)
  }
  getStateData = () => {
    data = store.getState()
  }

  render() {
    return (
      <div>接收到的数据是：{data.list}</div>
    )
  }
}

export default B;
